import './index.css';
import {useState, useEffect} from 'react';
import Header from './Header';
import BookList from './BookList';
import Addbook from './Addbook';

function App() {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState("");
  const apiUrl = 'http://127.0.0.1:8000/api/book/'



  useEffect(()=>{

      getBooks();

  },[])


  const getBooks = async () => {
      const response = await fetch(apiUrl);
      const data = await response.json();
      setBooks(data);

  }


  const deleteBook = async (id) => {
      const listBooks = books.filter(book => book.id !== id);
      setBooks(listBooks)

      fetch(`${apiUrl}delete/${id}`, {
        method: 'DELETE',
        'headers': {
            'Content-Type': 'application/json'
        }
    })
    }

    // const createBook = async (id) => {
    //   const listBooks = books.filter(book => book.id !== id);
    //   setBooks(listBooks)

    //   fetch(`${apiUrl}delete/${id}`, {
    //     method: 'POST',
    //     'headers': {
    //         'Content-Type': 'application/json'
    //     },
    //     body:JSON.stringify(book)
    // })
    // }

    const addBook = (book) => {
      const id = book.length ? books[books.length-1].id+1: 1;
      const newBook = {id, book };
      const listBooks = [...books, newBook]; 
      setNewBook(listBooks)
    }


    const handlesubmit = (e) => {
      e.preventDefault();
      if (!newBook) return;
      console.log(newBook)
        addBook(newBook)
        setNewBook('')

    }


  return (
        <div className="App">

          <Header/>

          <Addbook
            newBook={newBook}
            setNewBook ={setNewBook}
            handlesubmit ={handlesubmit}

          />

          <BookList
            books={books}
            setBooks={setBooks}
            deleteBook={deleteBook}

          />


      </div>


  );
}

export default App;
