import React from 'react';
import ReactDOM from 'react-dom/client';
import { 

  BrowserRouter,
  Routes,
  Route

} from "react-router-dom";

import './index.css';
import App from './App';
import Addbook from './Addbook';



  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState("");
  const [newBookDesc, setNewBookDesc] = useState("");
  const apiUrl = 'http://127.0.0.1:8000/api/book'

  const addBook = async (book_name, description) => {
  const id = book_name.length ? books[books.length-1].id+1: 1;
  const newBook = {id, book_name, description };
  const listBooks = [...books, newBook]; 

  fetch(`${apiUrl}/create/`, {
        method: 'POST',
        'headers': {
            'Content-Type': 'application/json'
        },
        body:JSON.stringify(newBook)
    })
}


const handlesubmit = (e) => {
  e.preventDefault();
  if (!newBook) return;
    addBook(newBook, newBookDesc)
    setNewBook('')
    setNewBookDesc('')

}


const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <BrowserRouter>
    <Routes>
      <Route path="/" element={<App />} />
      <Route path="add" element={<Addbook newBook={newBook}
            setNewBook ={setNewBook}
            newBookDesc={newBookDesc}
            setNewBookDesc={setNewBookDesc}
            handlesubmit ={handlesubmit}
 />} />
    </Routes> 
  </BrowserRouter>
);
