import React from 'react'
import { useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import axios from 'axios'

const Updatebook = (deleteBook) => {

    const AxiosBook = axios.create({
        baseURL:"http://127.0.0.1:8000/api"

      })
    const {id} = useParams()
    const [book, setBook]= useState("")
    const [bookDesc, setBookDesc]= useState("")


    useEffect(()=> {

        const fetchBook = async () =>{
            try{

                const response = await AxiosBook.get(`/book/${id}`)
                setBook(response.data);
            }catch(err){
                console.log(err.message)}
    }

    fetchBook();
  },[])
  const handleupdate = (e)=>{
    e.preventDefault();
    if (!book) return;
      updateBook(id, book, bookDesc)
  }
  const updateBook =  async(id, book, description) =>{
    const updates = {id, book, description}  
    await AxiosBook.put(`/book/update/${id}`, updates)

  } 

  return (
    <form className='form' onSubmit={handleupdate}>

    <input

        autoFocus
        id='name'
        type='text'
        placeholder={`${book.name}`}
        required
        value={book}
        onChange ={(e)=> {setBook({...book, 'book_name': e.target.value})}}

    /> <br/>
    <textarea

        placeholder={`${book.description}`}
        required
        value={bookDesc}
        onChange ={(e)=> {setBookDesc({...book, 'description': e.target.value})}}

    /> <br/>
    <button id='submit' type='submit'>Update Book</button>            
</form>
  )
}

export default Updatebook
