import './index.css';
import {useState, useEffect} from 'react';
import Header from './Header';
import BookList from './BookList';
import Addbook from './Addbook';
import axios from 'axios'
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom'



function App() {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState("");
  const [newBookDesc, setNewBookDesc] = useState("");
  const [showAdd, setShowAdd] = useState(false)

  const apiUrl = 'http://127.0.0.1:8000/api/book'




  // axios

  const AxiosBook = axios.create({
    baseURL:"http://127.0.0.1:8000/api"

  })


  useEffect(()=> {

    const fetchBooks = async () =>{
      try{
        const response = await AxiosBook.get("/book")
        setBooks(response.data);

      }
      catch(err){
        if(err.response){

          console.log(err.response.data)
          console.log(err.response.status)
          console.log(err.response.headers)}

        else{console.log(err.message)}

      }
    }

    fetchBooks();
  },[])




  // fetch

  useEffect(()=>{

    // getBooks();

  },[])


  const getBooks = async () => {
      const response = await fetch(apiUrl);
      const data = await response.json();
      setBooks(data);

  }


  const deleteBook = async (id) => {
      const listBooks = books.filter(book => book.id !== id);


    //   fetch(`${apiUrl}/delete/${id}`, {
    //     method: 'DELETE',
    //     'headers': {
    //         'Content-Type': 'application/json'
    //     }
    // })

    // axios 
    const response = await AxiosBook.delete(`/book/delete/${id}`)
    setBooks(listBooks)
    }

    const addBook = async (book_name, description) => {
      const id = books.length ? books[books.length-1].id+1: 1;
      const newBook = {id, book_name, description };


      // fetch(`${apiUrl}/create/`, {
      //       method: 'POST',
      //       'headers': {
      //           'Content-Type': 'application/json'
      //       },
      //       body:JSON.stringify(newBook)
      //   })

      // Axios

      try{
      const response = await AxiosBook.post('/book/create', newBook)
      const listBooks = [...books, newBook]; 
      setBooks(listBooks)
      }catch(err){
        console.log(err.message)

      }

    }


    const handlesubmit = (e) => {
      e.preventDefault();
      if (!newBook) return;
        addBook(newBook, newBookDesc)
        setNewBook('')
        setNewBookDesc('')

    }

  const handleview= () =>{
    setShowAdd(!showAdd)
  }


  return (

        <div className="App">

        <Router>

          <Routes>

          <Route path="/" element = {
          <Addbook
          newBook={newBook}
          setNewBook ={setNewBook}
          newBookDesc={newBookDesc}
          setNewBookDesc={setNewBookDesc}
          handlesubmit ={handlesubmit}

        />}/>


        </Routes>
        </Router>  

        {/* <button onClick={handleview}  id='toggle'>{showAdd ? 'Close': 'Add'}</button>

        {showAdd &&   <Addbook
          newBook={newBook}
          setNewBook ={setNewBook}
          newBookDesc={newBookDesc}
          setNewBookDesc={setNewBookDesc}
          handlesubmit ={handlesubmit}

        /> }

        {books.length ? 
             <BookList
             books={books}
             setBooks={setBooks}
             deleteBook={deleteBook}

           />:
           <h2>No Books</h2>
        }
           */}

      </div>


  );
}

export default App;
